import React from 'react';
// reactstrap components
import {
    Container,
    Row,
    Col
  } from "reactstrap";

class AboutUs extends React.Component {
    render(){
        return (
            <section className="section section-lg bg-gradient-default">
            <Container className="pt-30 pb-30">
              <Row className="text-center justify-content-center">
                <Col lg="10">
                  <h2 className="display-3 text-white">درباره ی ما</h2>
                  <p className="lead text-white">
                    همه چیز از دوران دانشجویی شروع شد و این که حساب و کتاب خرج و مخارج گروهی زمان بر و پیچیده بود. تصمیم گرفتیم راه حلی که این چند سال برای این مشکل  استفاده کردیم رو با شما به اشتراک بزاریم.
                  </p>
                </Col>
              </Row>
            </Container>
          </section>
        );
    }
}

export default AboutUs;
