import React from "react";
import { Col } from "reactstrap";
import CountUp from 'react-countup';

class StatisticUnit extends React.Component {
  constructor(){
    super();
    this.state={
      isShow: false
    };
    this.onChangeShowing = this.onChangeShowing.bind(this);
  }
  onChangeShowing(){
    // this.props.onChangeShowing();
    this.setState({isShow:!this.state.isShow});
  }
  // componentDidUpdate(prevState){
  //   if(prevState.isShow !== this.state.isShow)
  // }
  render() {
    return (
      <Col lg="3" xs="6" id='statis'>
        <div data-aos="fade-up"
             data-aos-duration="1000"
             data-aos-delay="200"
             className="px-0"
             >
          <img
            alt="..."
            className="rounded-circle statistic-circle-image"
            src={this.props.img_url}
            onClick={this.onChangeShowing}
          />
          <div className="pt-4 text-center ">

            <p
            className={`main-page-text ${this.props.isShow ? 'show' : 'not-show'}`}
            >
              <CountUp
              start={1}
              end={100}
              delay={1}
              duration={2}
              // onEnd={({pauseResume,reset, start, update) => start()}
              onStart={this.onChangeShowing}
              start={this.props.start}/>
              {` ${this.props.title}`}
            </p>
          </div>
        </div>
      </Col>
    );
  }
}

export default StatisticUnit;
