import React from "react";
// import Headroom from "./headroom/Headroom.js";
import {
  Collapse,
  NavbarBrand,
  Navbar,
  Nav,
  NavbarToggler,
  NavItem,
  Container,
  Row,
  Col,
  Button,
  NavLink,
  Badge
} from "reactstrap";
import {Link} from "react-router-dom";
import NavBarProfile from "./navbar-profile.js";
import NavBarButtons from "./navbar-buttons";
import "./navbar.css";
import { useState } from 'react';
class MyNavbar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      pageSelection: "my-account",
      navBarCollapse: false,
      collapsed:true,
      setCollapsed:true
    };
  }

  toggleNavBar = () => {
    this.setState({
      navBarCollapse: !this.state.navBarCollapse
    });
  };
  togglePage = state => {
    this.setState({
      pageSelection: state
    });
  };

  componentDidMount() {
    // let headroom = new Headroom(document.getElementById("navbar-main"));
    // initialise
    // headroom.init();
  }

  render() {
    const pageSelection = this.state.pageSelection;
    const {collapsed, setCollapsed}= this.state;
    const toggleNavbar = () => setCollapsed(!collapsed);

    let buttonsOrProfile;
    let links;
    if (this.props.isLogin === "true") {
      buttonsOrProfile = <NavBarProfile/>;
      links = (
        <div className="d-flex justify-content-center align-items-center">
          <Button
            color="link"
            className={
              pageSelection === "my-account"
                ? "navbar-link-active"
                : "navbar-link"
            }
            to="/my-account"
            tag={Link}
            onClick={() => this.togglePage("my-account")}
          >
            حساب من
          </Button>
          <Button
            className={
              pageSelection === "notifications"
                ? "navbar-link-active"
                : "navbar-link"
            }
            onClick={() => this.togglePage("notifications")}
            color="link"
            to="/notifications"
            tag={Link}
          >
            اعلام‌ها <Badge color="warning">4</Badge>
          </Button>

          <Button
            className={
              pageSelection === "my-groups"
                ? "navbar-link-active"
                : "navbar-link"
            }
            onClick={() => this.togglePage("my-groups")}
            color="link"
            to="/mygroups"
            tag={Link}
          >
            گروه‌های من
          </Button>
          <Button
            className={
              pageSelection === "report" ? "navbar-link-active" : "navbar-link"
            }
            onClick={() => this.togglePage("report")}
            color="link"
            tag={Link}
            disabled
          >
            گزارش
          </Button>
        </div>
      );
    } else {
      buttonsOrProfile = <NavBarButtons/>;
      links = null;
    }
    return (
      <div>
        <Navbar color="faded" light>
          <NavbarBrand href="/" className="mr-auto">reactstrap</NavbarBrand>
          <NavbarToggler onClick={toggleNavbar} className="mr-2" />
          <Collapse isOpen={!collapsed} navbar>
            <Nav navbar>
              <NavItem>
                <NavLink href="/components/">Components</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
            </Nav>
          </Collapse>
        </Navbar>
      </div>

    );
  }
}

export default MyNavbar;
