import React from "react";
import { Col, Row } from "reactstrap";

class GroupUnit extends React.Component {
  constructor(props){
    super(props);
    this.state={
      group: null
    };
  }
  componentDidMount(){
    this.setState({
      group: this.props.group
    });
  }
  render() {
    let g = this.state.group;
    return (
      <Col xs={"12"} sm={"6"} className="my-1 px-1">
        <Row className="py-1 justify-content-center group-unit-card">
        <Col  className="table-header-col px-0 check-width">
          <div
            className="p-0 px-1 custom-control custom-control-alternative custom-checkbox">
            <input
              className="custom-control-input"
              id={g ? g.id : '0'}
              type="checkbox"
              // checked={this.props.allCheckBoxesChecked}
              // onChange={() => this.props.handleAllCheckedChange()}
            />
            <label
              className="custom-control-label pr-1"
              htmlFor=" customCheckLogin"
            >
                <span className="signin-modal-text mr-3">
                </span>
            </label>
          </div>
        </Col>

          <Col xs="3" className="px-0">
            <img
              className="rounded-circle group-unit-circle-image"
              top
              src={this.props.imageUri}
              alt=""
            />
          </Col>
          <Col
            xs="7"
            className="group-unit-name my-auto d-block px-1"
          >
            <span className="group-unit-name-text d-block m-0">
{g ? g.name : 'hi'}
            </span>
            <span className="group-unit-username-text d-block m-0 mt-1">

            @</span>

          </Col>
        </Row>
      </Col>
    );
  }
}

export default GroupUnit;
