import React from "react";
import { Col, Card, CardBody, CardImg, CardText, Row } from "reactstrap";
class ServiceCard extends React.Component {
  render() {
    let content = null;
    if(this.props._direction === 'rtl'){
      content = (
        <Row>
          <Col lg="6" xs="12">
            <Card className="floating">
              <CardImg
                alt="..."
                src={require("../../assets/img/theme/img-2-1200x1000.jpg")}
                top
                width="100%"
              />
              <CardBody>
                {/* <CardTitle>Card title</CardTitle>*/}

                <CardText>استفاده از اون آسون و البته رایگانه</CardText>
              </CardBody>
            </Card>
          </Col>
          <Col lg="6" xs="12"></Col>
        </Row>
      );
    }else {
      content = (
        <Row>
        <Col lg="6" xs="12"></Col>
          <Col lg="6" xs="12">
            <Card className="floating">
              <CardImg
                alt="..."
                src={require("../../assets/img/theme/img-2-1200x1000.jpg")}
                top
                width="100%"
              />
              <CardBody>
                {/* <CardTitle>Card title</CardTitle>*/}

                <CardText>استفاده از اون آسون و البته رایگانه</CardText>
              </CardBody>
            </Card>
          </Col>

        </Row>
      );
    }
    return (
      <Col className="m-0 p-2" lg="12">
{content}
      </Col>
    );
  }
}

export default ServiceCard;
