import React from "react";
import {Button, NavLink, Row} from 'reactstrap'

import SignUpButton from "../register-button/register-button.js";
import RegisterLink from "../register-button/register-link.js";
import {Link} from "react-router-dom";

class NavBarButtons extends React.Component {
  handleSignIn = () => {
    this.props.togglePage('sign-in');
  };
  handleSignUp=()=>{
    this.props.togglePage('sign-up');
  };
  render() {
    return (
      <Row className=" m-0 d-lg-flex align-items-center">

        <NavLink
          className={`d-inline pr-lg-0 pl-3 pr-3 ${
            this.props.pageSelection === "sign-in"
              ? "nav-bar-link-active"
              : "navbar-link"
          }`}
          color="primary"
          id="navbar-signin"
          to="/signin"
          tag={Link}
          onClick={this.handleSignIn}
        >
          <i className="d-lg-none fa fa-sign-in mr-0 ml-2"/>
          ورود
        </NavLink>

        <p className="d-inline d-lg-none m-0 slash">/</p>

        <Button
          className="signup-button d-lg-inline d-none px-4 py-1"
          color="primary"
          to="/register"
          tag={Link}
          onClick={this.handleSignUp}
        >
          ثبت نام
        </Button>


        <RegisterLink
        pageSelection={this.props.pageSelection}
        togglePage={this.props.togglePage}
        />

      </Row>
    );
  }
}

export default NavBarButtons;
