import React from 'react'
import {Button, Col, NavLink, Row} from "reactstrap";
// import {Link} from "react-router-dom";
import './help.css'

class HelpTabs extends React.Component{
  constructor(props){
    super(props);
    this.state={
      selectedTab:'register-login'
    };
    this.handleChangeSelectedTab = this.handleChangeSelectedTab.bind(this);
  }
  handleChangeSelectedTab(page){
   this.setState({
     selectedTab: page,
   }); 
  }
  render() {
    return(
      <Row className="text-center justify-content-center">
        <Col lg="12">
          <Row className="text-center justify-content-center px-4">
            <Button
              color={'primary'}
              className={`help-btn-circle ${this.state.selectedTab === 'register-login' ? 'help-btn-circle-active' : ''}`}
              onClick={() => this.handleChangeSelectedTab("register-login")}
            >
              <p className={'help-btn-circle-text'}>ثبت نام</p>
              <p className={'help-btn-circle-text'}>ورود</p>
            </Button>

            <Button
              color={'primary'}
              className={`help-btn-circle ${this.state.selectedTab === 'groups' ? 'help-btn-circle-active' : ''}`}
              onClick={() => this.handleChangeSelectedTab("groups")}
            >
              گروه ها
            </Button>

            <Button
              color={'primary'}
              className={`help-btn-circle ${this.state.selectedTab === 'payments' ? 'help-btn-circle-active' : ''}`}
              onClick={() => this.handleChangeSelectedTab("payments")}
            >
              هزینه ها
            </Button>

            <Button
              color={'primary'}
              className={`help-btn-circle ${this.state.selectedTab === 'notifications' ? 'help-btn-circle-active' : ''}`}
              onClick={() => this.handleChangeSelectedTab("notifications")}
            >
              اعلام ها
            </Button>
          </Row>
          <hr className="mt-1 mb-4"/>
        </Col>
      </Row>
    );
  }
}

export default HelpTabs;
