import React from "react";
import { Link } from "react-router-dom";
import {
  NavLink,
  Modal,
  Card,
  CardHeader,
  CardBody,
  Button,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col
} from "reactstrap";

class SignupButton extends React.Component {
  state = {
    signupFormModal: false
  };
  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };

  render() {
    let _buttonOrLink;
    if (this.props.buttonOrLink === "button") {
      _buttonOrLink = (
        <Button
          className="btn-neutral btn btn-default"
          color="primary"
          type="button"
          onClick={() => this.toggleModal("signupFormModal")}
        >
          {this.props.title}
        </Button>
      );
    } else {
      _buttonOrLink = (
        <NavLink
          className="nav-link pl-3 pr-3 "
          color="default"
          onClick={() => this.toggleModal("signupFormModal")}
        >
          {this.props.title}
        </NavLink>
      );
    }
    return (
      <div>
        <div>{_buttonOrLink}</div>
        <Modal
          className="modal-dialog-centered"
          size="sm"
          isOpen={this.state.signupFormModal}
          toggle={() => this.toggleModal("signupFormModal")}
        >
          <div className="modal-body p-0">
            <Card className="bg-secondary shadow border-0">
              <CardHeader className="bg-transparent p-1">
                {" "}
                {/* <Row className="justify-content-center"> */}{" "}
                <div className="text-muted  mt-3 mb-2">
                  <h5 className="my-modal-header">ایجاد حساب کاربری</h5>{" "}
                </div>{" "}
                {/* </Row> */}{" "}
              </CardHeader>{" "}
              <CardBody className="px-lg-5 py-lg-3">
                <div className="btn-wrapper text-center">
                  <Button
                    className="btn-neutral btn-icon"
                    color="default"
                    href="#pablo"
                    onClick={e => e.preventDefault()}
                  >
                    <span className="btn-inner--icon">
                      <img
                        alt="..."
                        src={require("../../assets/img/icons/common/google.svg")}
                      />{" "}
                    </span>{" "}
                    <span className="btn-inner--text pr-2 pt-1 m-0">
                      {" "}
                      حساب گوگل{" "}
                    </span>{" "}
                  </Button>{" "}
                </div>
                <div className="text-center text-muted mt-4 mb-3">
                  <small> یا ورود با حساب کاربری </small>{" "}
                </div>{" "}
                <Form role="form">
                  <FormGroup>
                    <InputGroup className="input-group-alternative">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-email-83" />
                        </InputGroupText>{" "}
                      </InputGroupAddon>

                      <Input
                      className="form-control-alternative is-valid" 
                      placeholder="ایمیل"
                      type="email" />
                    </InputGroup>{" "}
                  </FormGroup>{" "}
                  <FormGroup className="mb-2">
                    <InputGroup className="input-group-alternative">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-lock-circle-open" />
                        </InputGroupText>{" "}
                      </InputGroupAddon>

                      <Input placeholder="رمز عبور" type="password" />
                    </InputGroup>{" "}
                  </FormGroup>{" "}
                  <FormGroup className="mb-2">
                    <InputGroup className="input-group-alternative">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-lock-circle-open" />
                        </InputGroupText>{" "}
                      </InputGroupAddon>

                      <Input placeholder="تایید رمز عبور" type="password" />
                    </InputGroup>{" "}
                  </FormGroup>{" "}
                  <div class="text-muted font-italic justify-content-center d-flex mt-0">
                    <small>
                      کیفیت رمز عبور:
                      <span class="text-success font-weight-700 mr-2">
                        قوی
                      </span>
                    </small>
                  </div>
                  <Row className="text-center">
                    <Col>
                      <Button
                      className="my-4"
                      color="primary"
                      to="/dashboard"
                      tag={Link}>
                        ایجاد
                      </Button>
                    </Col>
                  </Row>
                  <div class="text-muted justify-content-center d-flex mt-0">
                    <small>
                      <span class="text-warning font-weight-700 mr-2">
                        رمز عبور غلطه!
                      </span>
                    </small>
                  </div>
                </Form>
              </CardBody>
            </Card>
          </div>
        </Modal>
      </div>
    );
  }
}

export default SignupButton;
