import React from "react";
import { Row, Col, Button } from "reactstrap";

class NotificationsTableRow extends React.Component {
  render() {
    let dateCol = null;
    let requestCol = null;
    let actionCol = null;
    const rowData = this.props.rowData;

    if (rowData.type === "buy-list") {
      dateCol = (<Col lg="2">
          <span className="date-request">{rowData.dateRequest}</span>
      </Col>);
      requestCol = (
        <Col lg="8">
          <span className="user-sender">{rowData.sender}</span>
          یک لیست نیازمندی در گروه
          <span className="target-group">{rowData.targetGroup}</span>
          ایجاد کرده است.
        </Col>
      );
      actionCol = (
        <Col lg="2" className="justify-content-center d-flex">
          <Button className="notification-table-row-btn" color="primary">مشاهده لیست</Button>
        </Col>
      );
    } else if (rowData.type === "join-to-my-group") {
      dateCol = (<Col lg="2">
          <span className="date-request">{rowData.dateRequest}</span>
      </Col>);
      requestCol = (
        <Col lg="8">
          <span className="user-sender">{rowData.sender}</span>
          درخواست پیوستن به گروه
          <span className="target-group">{rowData.targetGroup}</span>
          را دارد.
        </Col>
      );
      actionCol = (
        <Col lg="2" className="justify-content-center d-flex">
          <Button className="notification-table-row-btn" color="primary">قبول</Button>
          <Button className="notification-table-row-btn" color="default">رد</Button>
        </Col>
      );
    } else if (rowData.type === "join-me-to-other-group") {
      dateCol = (<Col lg="2">
          <span className="date-request">{rowData.dateRequest}</span>
      </Col>);
      requestCol = (
        <Col lg="8" className="">
          شما درخواست پیوستن به گروه
          <span className="target-group">{rowData.targetGroup}</span>
          را از
          <span className="user-sender">{rowData.sender}</span>
          دارید.
        </Col>
      );
      actionCol = (
        <Col lg="2" className="justify-content-center d-flex">
          <Button className="notification-table-row-btn" color="default">لغو درخواست</Button>
        </Col>
      );
    } else if (rowData.type === "disjoin-from-my-group") {
      dateCol = (<Col lg="2">
          <span className="date-request">{rowData.dateRequest}</span>
      </Col>);
      requestCol = (
        <Col lg="8">
          <span className="user-sender">{rowData.sender}</span>
          درخواست تسویه حساب و خروج از گروه
          <span className="target-group">{rowData.targetGroup}</span>
          را دارد.
        </Col>
      );
      actionCol = (
        <Col lg="2" className="justify-content-center d-flex">
          <Button className="notification-table-row-btn" color="primary">قبول</Button>
          <Button className="notification-table-row-btn" color="default">رد</Button>
        </Col>
      );
    } else if (rowData.type === "disjoin-me-from-other-group") {
      dateCol = (<Col lg="2">
          <span className="date-request">{rowData.dateRequest}</span>
      </Col>);
      requestCol = (
        <Col lg="8" className="">
          شما درخواست تسویه حساب و خروج از گروه
          <span className="target-group">{rowData.targetGroup}</span>
          را از
          <span className="user-sender">{rowData.sender}</span>
          دارید.
        </Col>
      );
      actionCol = (
        <Col lg="2" className="justify-content-center d-flex">
          <Button className="notification-table-row-btn" color="default">لغو درخواست</Button>
        </Col>
      );
    } else {
      requestCol = <Col lg="8"></Col>;
      actionCol = <Col lg="4"></Col>;
    }

    return (
      <Row className="notifications-table-row">
      {dateCol}
        {requestCol}
        {actionCol}
        <Col sm="11" className="ntr-hr"></Col>
      </Row>
    );
  }
}

export default NotificationsTableRow;
