import React from "react";
import { Container, Row, Col, Button, Card, CardImg, Media } from "reactstrap";
import img1 from "../assets/img/theme/11.png";

class Features extends React.Component {
  constructor(prop) {
    super(prop);

    this.state = {
      featureSelectedNumber: 1
    };
  }

  toggleFeatureSelection = numberOfFeature => {
    this.setState({
      featureSelectedNumber: numberOfFeature
    });
  };

  render() {
    const featureSelectedNumber = this.state.featureSelectedNumber;
    var featureSelectedimg = null;
    if (featureSelectedNumber == 1) {
      featureSelectedimg = require("../assets/img/theme/1.png");
    } else if (featureSelectedNumber == 2) {
      featureSelectedimg = require("../assets/img/theme/11.png");
    } else if (featureSelectedNumber == 3) {
      featureSelectedimg = require("../assets/img/theme/111.png");
    } else {
      featureSelectedimg = require("../assets/img/theme/1.png");
    }

    return (
      <section className="section section-lg mt-1 pt-6 pb-6">
        <Container>
          <Row className="text-center justify-content-center">
            <p className="section-title">امکانات</p>
          </Row>
          <Row className="text-center justify-content-center px-2">
            <Col lg="3">
            <Row className="d-flex justify-content-center my-auto">
              <Button
                className={
                  featureSelectedNumber === 1
                    ? "features-btn-active"
                    : "features-btn"
                }
                color="primary"
                onClick={() => this.toggleFeatureSelection(1)}
              >
                01 سریع و آسان
              </Button>
              <Button
                className={
                  featureSelectedNumber === 2
                    ? "features-btn-active"
                    : "features-btn"
                }
                color="primary"
                onClick={() => this.toggleFeatureSelection(2)}
              >
                01 سریع و آسان
              </Button>
              <Button
                className={
                  featureSelectedNumber === 3
                    ? "features-btn-active"
                    : "features-btn"
                }
                color="primary"
                onClick={() => this.toggleFeatureSelection(3)}
              >
                01 سریع و آسان
              </Button>
              </Row>
            </Col>

            <Col lg="6">
              <img
              className="features-img"
              alt="..."
              src={featureSelectedimg} />
            </Col>

            <Col lg="3">
              <Button
                className={
                  featureSelectedNumber === 1
                    ? "features-btn-active"
                    : "features-btn"
                }
                color="primary"
                onClick={() => this.toggleFeatureSelection(1)}
              >
                01 سریع و آسان
              </Button>
              <Button
                className={
                  featureSelectedNumber === 2
                    ? "features-btn-active"
                    : "features-btn"
                }
                color="primary"
                onClick={() => this.toggleFeatureSelection(2)}
              >
                01 سریع و آسان
              </Button>
              <Button
                className={
                  featureSelectedNumber === 3
                    ? "features-btn-active"
                    : "features-btn"
                }
                color="primary"
                onClick={() => this.toggleFeatureSelection(3)}
              >
                01 سریع و آسان
              </Button>
            </Col>
          </Row>
        </Container>
      </section>
    );
  }
}

export default Features;
