import React from "react";
import Headroom from "./Headroom.js";
import {
  UncontrolledCollapse,
  NavbarBrand,
  Navbar,
  NavItem,
  Nav,
  Container,
  Row,
  Col,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";

import { Link } from "react-router-dom";

import SigninButton from "../signin-button/SigninButton.js";
import SignupButton from "../signup-button/SignupButton.js";
import NavbarOurSocialNetworks from "./NavbarOurSocialNetworks.js";

class MyNavbar extends React.Component {
  state = {
    formModal: false
  };
  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };
  componentDidMount() {
    let headroom = new Headroom(document.getElementById("navbar-main"));
    // initialise
    headroom.init();
  }
  render() {
    let buttonsOrProfile;
    if (this.props.isLogin === "true") {
      buttonsOrProfile = (
        <div>
          <Nav className="navbar-nav-hover align-items-lg-center" navbar>
            <UncontrolledDropdown nav>
              <DropdownToggle nav className="pt-0 pb-0">
              <div className="d-flex justify-content-center align-items-center">
              <span className="nav-link-inner--text ml-2 ">Mollaahmadi</span>
              <img
                    alt="..."
                    className="rounded-circle img-center img-fluid shadow shadow-lg--hover"
                    src={require("../../assets/img/theme/team-3-800x800.jpg")}
                    style={{ width: "40px" }}
                  />

                </div>
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem to="/login-page" tag={Link}>
                  حساب کاربری
                </DropdownItem>
                <DropdownItem to="/register-page" tag={Link}>
                  خروج
                </DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
          </Nav>
        </div>
      );
    } else {
      buttonsOrProfile = (
        <div className="d-flex">
          <NavItem className="d-none d-lg-inline mr-lg-4">
            <SigninButton title="ورود" buttonOrLink="link"></SigninButton>
          </NavItem>
          <NavItem className="d-none d-lg-inline p-1 ">
            <SignupButton title="ثبت نام" buttonOrLink="button" />
          </NavItem>
        </div>
      );
    }
    return (
      <header className="header-global">
        <Navbar
          className="navbar-main navbar-transparent navbar-light headroom"
          expand="lg"
          id="navbar-main"
        >
          <Container>
            {/* BRAND */}
            <NavbarBrand className="mr-0 pt-0 pb-0" to="/" tag={Link}>
              {/* <img
                  alt="..."
                  src={require("../../assets/img/brand/argon-react-white.png")}
              /> */}
              <h1>حسابِه</h1>
            </NavbarBrand>
            {/* TOGGLER */}{" "}
            <button className="navbar-toggler" id="navbar_global">
              <span className="navbar-toggler-icon" />
            </button>
            <UncontrolledCollapse navbar toggler="#navbar_global">
              <div className="navbar-collapse-header">
                <Row>
                  <Col className="collapse-brand" xs="6">
                    <Link to="/">
                      <img
                        alt="..."
                        src={require("../../assets/img/brand/argon-react.png")}
                      />
                    </Link>
                  </Col>

                  <Col className="collapse-close" xs="6">
                    <button className="navbar-toggler" id="navbar_global">
                      <span />
                      <span />
                    </button>
                  </Col>
                </Row>
              </div>
              <Nav className="align-items-lg-center mr-lg-auto" navbar>
                <NavbarOurSocialNetworks />
                {buttonsOrProfile}
              </Nav>
            </UncontrolledCollapse>
          </Container>
        </Navbar>
      </header>
    );
  }
}

export default MyNavbar;
