import React from 'react'
import {
  Badge,
  Button,
  Col,
  Row
} from "reactstrap"
import './table-filters.css'
// import Labels from '../../common/labels/labels'
import SelectionLabels from "../selection-labels/selection-labels";
import ScrollableHorizontal from "../../my-components/scrollable-horizontal/scrollable-horizontal";
import ScrollHorizontal from "../scroll-horizontal/scroll-horizontal";

class TableFilters extends React.Component {
  constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.prev=this.prev.bind(this);
    this.sideScroll = this.sideScroll.bind(this);
  }
  sideScroll(element,direction,speed,distance,step){
    let scrollAmount = 0;
    let slideTimer = setInterval(function(){
      if(direction === 'left'){
        element.scrollLeft -= step;
      } else {
        element.scrollLeft += step;
      }
      scrollAmount += step;
      if(scrollAmount >= distance){
        window.clearInterval(slideTimer);
      }
    }, speed);
  }
  next(){
    let container = document.getElementById('container');
    this.sideScroll(container,'right',25,100,10);
  }
  prev(){
    let container = document.getElementById('container');
    this.sideScroll(container,'left',25,100,10);
  }

  render() {

    let filters = [];

    this.props.filters.forEach(filter => {
      filters.push(
        <Button
          className={`table-filters-btn ${(this.props._selectedFilterID && this.props._selectedFilterID === filter.id) ? 'selected-btn' : ''}`}
          color='secondary'
          onClick={() => this.props.selectSort(filter.id)}
        >
          {filter.value}
        </Button>
      );
      filters.push(
        <Button
          className={`table-filters-btn ${(this.props._selectedFilterID && this.props._selectedFilterID === filter.id) ? 'selected-btn' : ''}`}
          color='secondary'
          onClick={() => this.props.selectSort(filter.id)}
        >
          {filter.value}
        </Button>
      );
    });
    return (

      <>

        <div id="container">
        {/*<span>ترتیب نمایش:</span>*/}
        <div className="filters-row" id="content">
          {/*<ScrollableHorizontal contents={filters}/>*/}
          {filters}
        </div>
        </div>
        <Button id="slideBack" type="button" onClick={this.prev}>Prev</Button>
        <Button id="slide" type="button" onClick={this.next}>Next</Button>

        <Row className={`labels-row ${(!this.props.labels || this.props.labels.length === 0) ? 'd-none' : ''}`}>
          <SelectionLabels
            labels={this.props.labels}
            currentUser={this.props.currentUser}
            justify={'center'}
            setLabels={this.setLabels}
            hasClose={false}
          />

        </Row>

      </>
    );
  }
}

export default TableFilters;
