import React from "react";
import {
  Container,
  Row,
  Col,
} from "reactstrap";
import AccountUnit from "./account-unit.js";


class Profile extends React.Component {
  constructor(props){
    super(props);
    this.state={
      account1: false,
      account2: false,
      account3:false,
      account4: false
    };
  }
  isBottom(el) {
    return el.getBoundingClientRect().top <= window.innerHeight;
  }

  componentDidMount() {
    document.addEventListener("scroll", this.trackScrolling);
  }

  componentWillUnmount() {
    document.removeEventListener("scroll", this.trackScrolling);
  }
  trackScrolling = () => {
    const wrappedElement1 = document.getElementById("account1");
    const wrappedElement2 = document.getElementById("account2");
    const wrappedElement3 = document.getElementById("account3");
    const wrappedElement4 = document.getElementById("account3");
    if (this.isBottom(wrappedElement1)) {
      this.setState({
        account1: true,
      });
    }
    if (this.isBottom(wrappedElement2)) {
      this.setState({
        account2: true,
      });
    }
    if (this.isBottom(wrappedElement3)) {
      this.setState({
        account3: true,
      });
    }
    if (this.isBottom(wrappedElement4)) {
      this.setState({
        account4: true,
      });
    }
  };

  render() {
    return (
      <Container>
        <Row className='m-0'>
          <Col lg="12">
            <Row className="m-0 d-flex justify-content-center">
              <AccountUnit
                _id='account1'
                isShow={this.state.account1}
                _text="میزان هزینه شخصی"
                cost='21100'
                imageUri={require("../../assets/img/theme/team-4-800x800.jpg")}
              />

              <AccountUnit
                _id='account2'
                isShow={this.state.account2}
                _text="میزان هزینه گروهی"
                cost='2900'
                imageUri={require("../../assets/img/theme/team-4-800x800.jpg")}
              />

              <AccountUnit
                _id='account3'
                isShow={this.state.account3}
                _text="میزان مصرف گروهی"
                cost='1111'
                imageUri={require("../../assets/img/theme/team-4-800x800.jpg")}
              />

              <AccountUnit
                _id='account4'
                isShow={this.state.account4}
                _text="وضعیت نهایی"
                cost='2341'
                imageUri={require("../../assets/img/theme/team-4-800x800.jpg")}
              />
            </Row>
          </Col>
        </Row>
      </Container>

    );
  }
}

export default Profile;
