import React from 'react';
import {DropdownMenu, DropdownToggle, Dropdown} from 'reactstrap';

class InputAutoSuggestion extends React.Component {
  constructor() {
    super();
    this.state = {
      value: '',
      suggestions: []
    };
  }

  onChange = (event, {newValue})=>{
    this.setState({value: newValue});
  };

  onSuggestionFetchRequested = ({value}) => {
    this.setState({suggestions: getSuggestions(value)});
  };

  onSuggestionClearRequested = () => {
    this.setState({suggestions: []});
  };

  render(){
    const {value, suggestions} = this.state;

    const inputProps = {
      placeholder: 'jijijijij',
      value,
      onChange: this.onChange
    };

    return(
      <Autosuggest
        suggestions={suggestions}
        onSuggestionFetchRequested={this.onSuggestionFetchRequested}
        onSuggestionClearRequested = {this.onSuggestionClearRequested}
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
      />
    );
  }
}

export default InputAutoSuggestion;
