  render() {
    let value01 = this.state.value01;

    return (
      <div className="section">
      <Container >
        <Row className="mx-0 mt-5 d-flex justify-content-center">
          <p className="section-title">
          به حسابِه بپیوندید
          </p>
          <CountUp delay={5} end={100} />
        </Row>
        <Row className="mx-0 my-1" id='statis'>
          <StatisticUnit
            text={`${value01} کاربر`}
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
          <StatisticUnit
            text="۴۰ گروه"
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
          <StatisticUnit
            text="۴۰۰ تراکنش"
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
          <StatisticUnit
            text="۱۰۰ کاربر فعال"
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
        </Row>
      </Container>
      </div>
    );
  }
}

export default OurStatistics;
