import React from "react";

// reactstrap components
import { Button, Container, Row, Col, Modal } from "reactstrap";
// import SignupButton from '../common/signup-button/SignupButton.js';
import SigninButton from "../common/signin-button/SigninButton.js";

class Landing extends React.Component {
  state = {
    exampleModal: false
  };
  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };
  render() {
    return (
      <div className="position-relative">
        {/* shape Hero */}
        <section className="section section-lg section-shaped pb-200">
          <div className="shape shape-style-1 shape-default">
            {/* <span /> */}
            {/* <span /> */}
            {/* <span />                */}
            {/* <span /> */}
            {/* <span /> */}
            {/* <span /> */}
            {/* <span />                 */}
          </div>
          <Container className="py-lg-md d-flex">
            <div className="col px-0 ">
              <Row className="text-center">
                <Col>
                  <h2 className="display-3 text-white">
                    حساب،
                    <h1 className="h1-hesabeh">حسابِه</h1>
                    ...
                  </h2>
                  <p className="lead text-white">
                    اگه دانشجویی، اهل سفری یا با دوستات زندگی می‌کنی و می‌خوای
                    حساب کتاب خرج و مخارج دستت باشه، یه حساب کاربری ایجاد کن،
                    گروه بساز، دوستات رو اضافه کن و مخارج رو مدیریت کن.
                  </p>
                  <div className="btn-wrapper">
                    <SigninButton title="شروع" buttonOrLink="button" />
                  </div>
                </Col>
              </Row>
            </div>
          </Container>
        </section>
        {/* 1st Hero Variation */}
      </div>
    );
  }
}

export default Landing;
