import React from 'react';
import {NavLink, Modal, Card, CardHeader, CardBody, Button,
FormGroup,
Form,
Input,
InputGroupAddon,
InputGroupText,
InputGroup,Row,Col} from 'reactstrap';

class SigninButton extends React.Component {
  constructor() {

  }

  render(){
    return(
      <div>
        <NavLink
          className="nav-link pl-3 pr-3 "
          color="default"
          onClick={() => this.toggleModal("loginFormModal")}
        >
          {/* <span className="nav-link-inner--text ml-1"> */}
          ورود {/* </span> */}{" "}
        </NavLink>{" "}
        <Modal
          className="modal-dialog-centered"
          size="sm"
          isOpen={this.state.loginFormModal}
          toggle={() => this.toggleModal("loginFormModal")}
        >
          <div className="modal-body p-0">
            <Card className="bg-secondary shadow border-0">
              <CardHeader className="bg-transparent p-1">
                {" "}
                {/* <Row className="justify-content-center"> */}{" "}
                <div className="text-muted  mt-3 mb-2">
                  <h5 className="my-modal-header"> ورود </h5>{" "}
                </div>{" "}
                {/* </Row> */}{" "}
              </CardHeader>{" "}
              <CardBody className="px-lg-5 py-lg-3">
                <div className="btn-wrapper text-center">
                  <Button
                    className="btn-neutral btn-icon"
                    color="default"
                    href="#pablo"
                    onClick={e => e.preventDefault()}
                  >
                    <span className="btn-inner--icon">
                      <img
                        alt="..."
                        src={require("../../assets/img/icons/common/google.svg")}
                      />{" "}
                    </span>{" "}
                    <span className="btn-inner--text pr-2 pt-1 m-0">
                      {" "}
                      حساب گوگل{" "}
                    </span>{" "}
                  </Button>{" "}
                </div>
                <div className="text-center text-muted mt-4 mb-3">
                  <small> یا ورود با حساب کاربری </small>{" "}
                </div>{" "}
                <Form role="form">
                  <FormGroup className="mb-3">
                    <InputGroup className="input-group-alternative">

                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="ni ni-email-83" />
                      </InputGroupText>{" "}
                    </InputGroupAddon>

                      <Input
                        placeholder="ایمیل / نام کاربری"
                        type="email"
                      />


                    </InputGroup>{" "}
                  </FormGroup>{" "}
                  <FormGroup>
                    <InputGroup className="input-group-alternative">

                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="ni ni-lock-circle-open" />
                      </InputGroupText>{" "}
                    </InputGroupAddon>

                      <Input placeholder="رمز عبور" type="password" />


                    </InputGroup>{" "}
                  </FormGroup>{" "}
                  <div className="custom-control custom-control-alternative custom-checkbox">
                    <input
                      className="custom-control-input"
                      id=" customCheckLogin"
                      type="checkbox"
                    />
                    <label
                      className="custom-control-label pr-1"
                      htmlFor=" customCheckLogin"
                    >
                      <span className="text-muted mr-4">
                        {" "}
                        مرا به خاطر بسپار{" "}
                      </span>{" "}
                    </label>{" "}
                  </div>{" "}
                  <Row className="text-center">
                    <Col>
                      <Button
                        className="my-4"
                        color="primary"
                        type="button"
                      >
                        ورود{" "}
                      </Button>{" "}
                    </Col>{" "}
                  </Row>{" "}
                  <Row />{" "}
                </Form>{" "}
              </CardBody>{" "}
            </Card>{" "}
          </div>{" "}
        </Modal>
      </div>
    );
  }
}

export default SigninButton;
