import React from "react";
import Headroom from "./Headroom.js";
import {
  Button,
  UncontrolledCollapse,
  NavbarBrand,
  Navbar,
  NavItem,
  NavLink,
  Nav,
  Container,
  Row,
  Col,
  UncontrolledTooltip
} from "reactstrap";

import {
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Modal
} from "reactstrap";

import { Link } from "react-router-dom";

import SigninButton from '../signin-button/SigninButton.js';

class MyNavbar extends React.Component {


  state = {
    formModal: false
  };
  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };
  componentDidMount() {
    let headroom = new Headroom(document.getElementById("navbar-main"));
    // initialise
    headroom.init();
  }
  render() {
    return (
      <header className="header-global">
      <Navbar
        className="navbar-main navbar-transparent navbar-light headroom"
        expand="lg"
        id="navbar-main"
      >
        <Container>
          {" "}
          {/* BRAND */}{" "}
          <NavbarBrand className="mr-0" to="/" tag={Link}>
            {" "}
            {/* <img
                                 alt="..."
                                 src={require("../../assets/img/brand/argon-react-white.png")}
                               /> */}{" "}
            <h1>حسابِه</h1>{" "}
          </NavbarBrand>
          {/* TOGGLER */}{" "}
          <button className="navbar-toggler" id="navbar_global">
            <span className="navbar-toggler-icon" />
          </button>
          <UncontrolledCollapse navbar toggler="#navbar_global">
            <div className="navbar-collapse-header">
              <Row>
                <Col className="collapse-brand" xs="6">
                  <Link to="/">
                    <img
                      alt="..."
                      src={require("../../assets/img/brand/argon-react.png")}
                    />{" "}
                  </Link>{" "}
                </Col>

                <Col className="collapse-close" xs="6">
                  <button className="navbar-toggler" id="navbar_global">
                    <span />
                    <span />
                  </button>{" "}
                </Col>
              </Row>{" "}
            </div>
            <Nav className="align-items-lg-center mr-lg-auto" navbar>
              <NavItem>
                <NavLink
                  className="nav-link-icon"
                  href="https://www.facebook.com/creativetim"
                  id="tooltip333589074"
                  target="_blank"
                >
                  <i className="fa fa-facebook-square" />
                  <span className="nav-link-inner--text d-lg-none ml-2">
                    Facebook{" "}
                  </span>{" "}
                </NavLink>{" "}
                <UncontrolledTooltip delay={0} target="tooltip333589074">
                  Like us on Facebook{" "}
                </UncontrolledTooltip>{" "}
              </NavItem>
              <NavItem>
                <NavLink
                  className="nav-link-icon"
                  href="https://www.instagram.com/creativetimofficial"
                  id="tooltip356693867"
                  target="_blank"
                >
                  <i className="fa fa-instagram" />
                  <span className="nav-link-inner--text d-lg-none ml-2">
                    Instagram{" "}
                  </span>{" "}
                </NavLink>{" "}
                <UncontrolledTooltip delay={0} target="tooltip356693867">
                  Follow us on Instagram{" "}
                </UncontrolledTooltip>{" "}
              </NavItem>
              <NavItem>
                <NavLink
                  className="nav-link-icon"
                  href="https://twitter.com/creativetim"
                  id="tooltip184698705"
                  target="_blank"
                >
                  <i className="fa fa-twitter-square" />
                  <span className="nav-link-inner--text d-lg-none ml-2">
                    Twitter{" "}
                  </span>{" "}
                </NavLink>{" "}
                <UncontrolledTooltip delay={0} target="tooltip184698705">
                  Follow us on Twitter{" "}
                </UncontrolledTooltip>{" "}
              </NavItem>
              <NavItem>
                <NavLink
                  className="nav-link-icon"
                  href="https://github.com/creativetimofficial/argon-design-system-react"
                  id="tooltip112445449"
                  target="_blank"
                >
                  <i className="fa fa-github" />
                  <span className="nav-link-inner--text d-lg-none ml-2">
                    Github{" "}
                  </span>{" "}
                </NavLink>{" "}
                <UncontrolledTooltip delay={0} target="tooltip112445449">
                  Star us on Github{" "}
                </UncontrolledTooltip>{" "}
              </NavItem>
              <NavItem className="d-none d-lg-inline mr-lg-4">
<div>
              <SigninButton title="hi"></SigninButton>

                </div>
              </NavItem>{" "}

              <NavItem className="d-none d-lg-block ">
                <div>
                  <Button
                    className="btn-neutral pl-3 pr-3"
                    color="default"
                    onClick={() => this.toggleModal("signupFormModal")}
                  >
                    {/* <span className="nav-link-inner--text ml-1"> */}
                    ثبت نام {/* </span> */}{" "}
                  </Button>{" "}
                  <Modal
                    className="modal-dialog-centered"
                    size="sm"
                    isOpen={this.state.signupFormModal}
                    toggle={() => this.toggleModal("signupFormModal")}
                  >
                    <div className="modal-body p-0">
                      <Card className="bg-secondary shadow border-0">
                        <CardHeader className="bg-transparent p-1">
                          {" "}
                          {/* <Row className="justify-content-center"> */}{" "}
                          <div className="text-muted  mt-3 mb-2">
                            <h5 className="my-modal-header">ایجاد حساب کاربری</h5>{" "}
                          </div>{" "}
