import React from 'react';
import {DropdownMenu, DropdownToggle, Dropdown, Input} from 'reactstrap';
import Chip from '../chip/chip'
class InputAutoSuggestion extends React.Component {
  constructor() {
    super();
    this.state = {
      value: '',
      dropdownOpen: true
    };
    this.handleOnChange = this.handleOnChange.bind(this);
  }

  handleOnChange(event){
    this.props.handleInputChange(event.target.value);
  }

  render(){

    return(
      <Dropdown class={'choose-user-dropdown'} isOpen={this.state.dropdownOpen}>
        <DropdownToggle
        tag={"div"}>

                              <Input
                                className="audit-input"
                                placeholder="ایمیل/ نام کاربری / شماره همراه اعضا"
                                type="text"
                                onChange={this.handleOnChange}
                              />
        </DropdownToggle>
        <DropdownMenu>
        <Chip
        class="mx-2 my-1"
        haveCloseButton="true"
          avatarSrc={require("../../../assets/img/users/user01.jpg")}
          label="محمد ملااحمدی"
          // onDelete={() => this.deleteUser()}
        />
        </DropdownMenu>
      </Dropdown>
    );
  }
}

export default InputAutoSuggestion;
