import React from 'react'
import {Badge, Col, Row} from "reactstrap";

export default class SelectionLabels extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    let labels = [];
    if (this.props.currentUser.labels) {
      this.props.currentUser.labels.forEach(label => {
        labels.push(
          <Badge
            style={{
              background: `rgba(${label.color.r},${label.color.g},${label.color.b},${label.color.a})`,
              color: `${label.isTextColorDark ? 'rgb(0,0,0,1)' : 'rgb(255,255,255,1)'}`
            }}
            color="primary"
            className="profile-label align-self-center px-2">
            {label.name}
            <i className={`fa fa-close mr-3 profile-label-close-btn ${!this.props.hasClose ? 'd-none' : ''}`}
              // onClick={() => this.handleDeleteLabel(label.id)}
            />
          </Badge>
        );
      });
    }
    return (
      <Col lg="12" className="m-0">
        <Row className="m-0 d-flex justify-content-right">
          <p className="p-0 mx-3 my-auto consumers-text">
            {this.props.title}
          </p>
          {labels}
        </Row>
      </Col>
    );
  }
}
