import React from "react";
import { Row, Col, Container } from "reactstrap";

import Onboarding from "./Onboarding.js";
import './Learning.css';

class Learning extends React.Component {
  render() {
    return (
      <section className="section section-lg bg-gradient-default pb-6 pt-5">
        <Container className="pt-30 pb-30">
          <Row className="text-center justify-content-center">
            <Col lg="10">
            <h2 className="display-3 text-white mb-5">همین حالا شروع کنید
            </h2>
              <Row>
                <Onboarding
                  _text="ثبت نام و ورود به حساب کاربری"
                  imageUri={require("../assets/img/theme/team-1-800x800.jpg")}
                />
                <Onboarding
                  _text="افزودن خرج و مخارج"
                  imageUri={require("../assets/img/theme/team-1-800x800.jpg")}
                />
                <Onboarding
                  _text="بررسی وضعیت مالی"
                  imageUri={require("../assets/img/theme/team-1-800x800.jpg")}
                />
                <Onboarding
                  _text="پرداخت هزینه‌ها"
                  imageUri={require("../assets/img/theme/team-1-800x800.jpg")}
                />
              </Row>
            </Col>
          </Row>
        </Container>
      </section>
    );
  }
}

export default Learning;
