import React from 'react'
import {Button, Col, NavLink, Row} from "reactstrap";
// import {Link} from "react-router-dom";
import './help.css'

class HelpTabs extends React.Component{
  constructor(props){
    super(props);
    this.state={
      selectedTab:'register-login'
    };
    this.handleChangeSelectedTab = this.handleChangeSelectedTab.bind(this);
  }
  handleChangeSelectedTab(page){
   this.setState({
     selectedTab: page,
   }); 
  }
  render() {
    return(
      <Row className="text-center justify-content-center">
        <Col lg="12">
          <Row className="text-center justify-content-center px-4">
            <NavLink
              activeClassName="link-active"
              className={`help-tabs-nav-link ${this.state.selectedTab === 'register-login' ? 'help-tabs-nav-link-selected' : ''}`}
              color="primary"
              onClick={() => this.handleChangeSelectedTab("register-login")}
            >
              ثبت نام و ورود
            </NavLink>

            <NavLink
              activeClassName="link-active"
              className={`help-tabs-nav-link ${this.state.selectedTab === 'groups' ? 'help-tabs-nav-link-selected' : ''}`}
              color="primary"
              onClick={() => this.handleChangeSelectedTab("groups")}
            >
              گروه ها
            </NavLink>
            <Button
              className="p-0 btn-circle m-0"
              onClick={this.handleChangeTextColor}>
              <i className="fa fa-pencil"/>
            </Button>
            <NavLink
              activeClassName="link-active"
              className={`help-tabs-nav-link ${this.state.selectedTab === 'payments' ? 'help-tabs-nav-link-selected' : ''}`}
              color="primary"
              onClick={() => this.handleChangeSelectedTab("payments")}
            >
              هزینه ها
            </NavLink>
            <NavLink
              activeClassName="link-active"
              className={`help-tabs-nav-link ${this.state.selectedTab === 'notifications' ? 'help-tabs-nav-link-selected' : ''}`}
              color="primary"
              onClick={() => this.handleChangeSelectedTab("notifications")}
            >
              اعلام ها
            </NavLink>
          </Row>
          <hr className="mt-1 mb-4"/>
        </Col>
      </Row>
    );
  }
}

export default HelpTabs;
