import React from 'react';
import {Row, Col} from 'reactstrap'
import Chip from '../../common/chip/chip'

class PaymentsTableRow extends React.Component {
  render() {

    let consumers = [];
    this.props.data.consumers.forEach(consumer => {
      consumers.push(<Chip
        link="/mollaahmadi.ir"
        haveCloseButton="false"
        avatarSrc={require("../../assets/img/users/user01.jpg")}
        label={consumer.username}
        class='mb-1 mx-1'
        // onDelete={() => this.deleteUser()}
      />);
    });
    return (
      <>


        <Col xs='12' sm='6' lg='12' className='p-0 m-3 m-lg-0'>
          <Row className="m-0">
            <Col xs='12' lg='auto'
                 className="payments-table-row-col-xs justify-content-lg-center justify-content-end px-0">
              <span className='d-lg-none ml-auto'>{this.props.data.date}</span>
              <div
                className="p-0 px-1 custom-control custom-control-alternative custom-checkbox">
                <input
                  className="custom-control-input"
                  id={this.props.data.id}
                  type="checkbox"
                />
                <label
                  className="custom-control-label pr-1"
                  htmlFor={this.props.data.id}
                >
                  <span className="signin-modal-text mr-3">
                  </span>
                </label>
              </div>
            </Col>


            <Col lg="1" className="d-none d-lg-flex payments-table-row-col date-request">
              <span>{this.props.data.date}</span>
            </Col>

            <Col xs='6' lg="3" className=" payments-table-row-col my-auto">
              <span className='d-lg-none ml-2'>نام: </span>
              <span>
                {this.props.data.name}
              </span>
            </Col>
            <Col xs='6' lg="2" className="payments-table-row-col my-auto">
              <span className='d-lg-none ml-2'>قیمت: </span>
              <span>{this.props.data.value}</span>
            </Col>
            <Col xs='12' className="d-lg-none payments-table-row-col-xs my-auto">
              <span className='ml-2'>توضیح: </span>
              <span>{this.props.data.description}</span>
            </Col>

            <Col xs='12' lg="3" className="payments-table-row-col my-auto">
              <span className='d-lg-none ml-2'>انجام دهندگان: </span>

              <Chip
                link="/mollaahmadi.ir"
                haveCloseButton="false"
                avatarSrc={require("../../assets/img/users/user01.jpg")}
                label={this.props.data.group}
                // onDelete={() => this.deleteUser()}
              />

            </Col>

            <Col lg="2" className="payments-table-row-col my-auto flex-grow-1">
              <span className='d-lg-none ml-2 w-auto'>مصرف کنندگان: </span>
              <Row className="mx-0 consumers">{consumers}</Row>
            </Col>

            <Col sm="12" className="payments-table-row-line"/>
          </Row>
        </Col>


      </>
    );
  }
}

export default PaymentsTableRow;
