import React from 'react'
import {Col, Row} from "reactstrap";

class PaymentsTableHeader extends React.Component {
  constructor(props){
    super(props);
    this.handleChecked = this.handleChecked.bind(this);
  }
  handleChecked(){
    this.props.handleAllChecked();
  }
  render() {
    return (
      <Col lg='12' className='d-none d-lg-block px-0'>
        <Row className="table-header m-0">
          <Col  className="table-header-col px-0 check-width">
            <div
              className="p-0 px-1 custom-control custom-control-alternative custom-checkbox">
              <input
                className="custom-control-input"
                id=" customCheckLogin"
                type="checkbox"
                onChange={() => this.props.handleAllChecked()}
              />
              <label
                className="custom-control-label pr-1"
                htmlFor=" customCheckLogin"
              >
                  <span className="signin-modal-text mr-3">
                  </span>
              </label>
            </div>
          </Col>

          <Col lg="1" className="table-header-col">
            <p>تاریخ</p>
          </Col>
          <Col lg="3" className="table-header-col">
            <p>نام</p>
          </Col>
          <Col lg="2" className="table-header-col">
            <p>قیمت</p>
          </Col>
          <Col lg="3" className="table-header-col">
            <p>انجام دهندگان</p>
          </Col>
          <Col lg="2" className="table-header-col flex-grow-1">
            <p>مصرف کنندگان</p>
          </Col>
          <Col sm="12" className="table-header-line"/>
        </Row>
      </Col>
    );
  }
}

export default PaymentsTableHeader;
