import React from "react";
import {
  Badge,
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardImg,
  CardTitle,
  CardText
} from "reactstrap";
class OurServices extends React.Component {
  render() {
    return (
      <section className="section section-lg mt-1">
        <Container>
          <Row className="text-center justify-content-center">
            <h2 className="display-3 pb-3 ">چرا حسابه؟ </h2>
          </Row>
          <Row className=" text-center justify-content-center">
            <Col className="m-0 p-2" md="3" sm="4">
              <Card  className="floating">
                <CardImg
                  alt="..."
                  src={require("../assets/img/theme/img-2-1200x1000.jpg")}
                  top
                  width="100%"
                />
                <CardBody>
                  {/* <CardTitle>Card title</CardTitle>*/}

                  <CardText>استفاده از اون آسون و البته رایگانه</CardText>
                </CardBody>
              </Card>
            </Col>

          </Row>
        </Container>
      </section>
    );
  }
}

export default OurServices;
