import React from 'react'
import {
  Badge,
  Button,
  Col,
  Row
} from "reactstrap"
import './table-filters.css'
// import Labels from '../../common/labels/labels'
import SelectionLabels from "../selection-labels/selection-labels";
import ScrollableHorizontal from "../../my-components/scrollable-horizontal/scrollable-horizontal";

class TableFilters extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedFilterID: null,
    };
  }

  selectSort = (ID) => {
    this.setState({
      selectedFilterID: ID
    });
  };

  componentDidMount() {
    if(this.props._selectedFilterID){
      this.setState({
        selectedFilterID: this.props._selectedFilterID,
      });
    }
  }

  render() {

    let filters = [];

    this.props.filters.forEach(filter => {
      filters.push(
        <Button
          className={`table-filters-btn ${(this.state.selectedFilterID && this.state.selectedFilterID === filter.id) ? 'selected-btn' : ''}`}
          color='secondary'
          onClick={() => this.selectSort(filter.id)}
        >
          {filter.value}
        </Button>
      );
    });
    return (

      <>
        {/*<span>ترتیب نمایش:</span>*/}
        <Row className="filters-row">
          {/*<ScrollableHorizontal contents={filters}/>*/}
          {filters}
        </Row>

        <Row className={`labels-row ${(!this.props.labels || this.props.labels.length === 0) ? 'd-none' : ''}`}>
          <SelectionLabels
            labels={this.props.labels}
            currentUser={this.props.currentUser}
            justify={'center'}
            setLabels={this.setLabels}
            hasClose={false}
          />

        </Row>

      </>
    );
  }
}

export default TableFilters;
