componentDidMount(){
  configureAnchors({offset: 0, scrollDuration: 2000});
  if(this.props.goto === 'features'){
    goToAnchor('features');
  }}
  render() {
    // if(this.props.goto === 'features'){
    //   goToAnchor('features');
    // }
    let mainPage = null;
    if (this.props.isLogin === "false") {
      mainPage = (
        <div>
          <Landing onChangePage={this.props.onChangePage}/>
          <AboutUs />
          <OnBoarding />
          <Features />
          <OurStatistics />
        </div>
      );
    } else {
      mainPage = <div/>;
    }
    return (
      <div>
        {mainPage}
      </div>
    );
  }
}



