import React from 'react'
import './scrollable.css'

export default class ScrollableHorizontal extends React.Component {
  render() {
    let contents = [];
    if (this.props.contents) {
      this.props.contents.forEach(content => {
        contents.push(
          <div className={'scrollable-card--content'}>
            {content}
          </div>
        );
      });
    }
    return (
      <div className={'scrollable-body'}>
        <div className={'scroll-arrows-layer'}>
          <div className={'right-arrow'}>
            <i className={'fa fa-arrow-left arrow'}/>
          </div>
          <div className={'left-arrow'}>
            <i className={'fa-2x fa-chevron-left arrow'}/>
          </div>
        </div>
        <section className={'scrollable-card'}>
          {contents}
        </section>
      </div>
    );
  }
}
