import React from "react";
import { Row, Container } from "reactstrap";
import StatisticUnit from "./StatisticUnit.js";


class OurStatistics extends React.Component {
  constructor(props){
    super(props);
    this.state={
      value01:'123456'
    };
    this.handleScroll = this.handleScroll.bind(this);
  }
  handleScroll(){
    this.setState({
      value01: '12'
    });
  }
  render() {
    let value01 = this.state.value01;

    return (
      <div className="section">
      <Container >
        <Row className="mx-0 mt-5 d-flex justify-content-center">
          <p className="section-title">
          به حسابِه بپیوندید
          </p>
        </Row>
        <Row className="mx-0 my-1">
          <StatisticUnit
            text={value01}
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
            onScroll={this.handleScroll}
          />
          <StatisticUnit
            text="۴۰ گروه"
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
          <StatisticUnit
            text="۴۰۰ تراکنش"
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
          <StatisticUnit
            text="۱۰۰ کاربر فعال"
            img_url={require("../../assets/img/theme/team-4-800x800.jpg")}
          />
        </Row>
      </Container>
      </div>
    );
  }
}

export default OurStatistics;
