import React from "react";
import { Link } from "react-router-dom";
import {
  Modal,
  Card,
  CardHeader,
  CardBody,
  Button,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  Collapse
} from "reactstrap";
import "../modal.css";
import "../navbar/navbar.css"

class SignupButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      signupFormModal: false,
      email: {
        value: "",
        valid: false,
        collapse: false
      },
      password: {
        value: "",
        valid: false,
        collapse: false
      },
      confirmPassword: {
        value: "",
        valid: false,
        collapse: false
      }
    };
  }
  changeHandler = event => {
    this.setState({
      [event.target.name]: {
        value: event.target.value,
        valid: event.target.valid,
        collapse: event.target.collapse
      }
    });
  };
  toggle = state => {
    this.setState({
      [state]: { collapse: !this.state[state].collapse }
    });
  };
  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };

  render() {
    return (
      <div>
        <div>
          <Button
            className="signup-button"
            color="primary"
            type="button"
            onClick={() => this.toggleModal("signupFormModal")}
          >
            ثبت نام
          </Button>
        </div>
        <Modal
          className="modal-dialog-centered signup-modal"
          size="sm"
          isOpen={this.state.signupFormModal}
          toggle={() => this.toggleModal("signupFormModal")}
        >
          <div className="modal-body p-0">
            <Card className="bg-transparent shadow border-0">
              <CardHeader className="bg-transparent p-1 signup-modal-header">
                {/* <Row className="justify-content-center"> */}
                <div className="text-muted  mt-3 mb-2">
                  <h5>ایجاد حساب کاربری</h5>
                </div>
                {/* </Row> */}
              </CardHeader>
              <CardBody className="px-lg-5 py-lg-3">
                <div className="btn-wrapper text-center">
                  <Button
                    className="btn-secondary btn-icon"
                    href="#pablo"
                    onClick={() => this.toggle("password")}
                    name="email"
                  >
                    <span className="btn-inner--icon">
                      <img
                        alt="..."
                        src={require("../../assets/img/icons/common/google.svg")}
                      />
                    </span>
                    <span className="btn-inner--text pr-2 pt-1 m-0">
                      حساب گوگل
                    </span>
                  </Button>
                </div>
                <div className="text-center signup-modal-text mt-4 mb-3">
                  <small>یا ثبت نام کنید:</small>
                </div>
                <Form role="form" className="signup-form">
                  <FormGroup>
                    <InputGroup className="input-group-alternative ">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-email-83" />
                        </InputGroupText>
                      </InputGroupAddon>

                      <Input
                        className="signup-modal-input"
                        placeholder="ایمیل"
                        type="email"
                        onChange={this.changeHandler}
                        name="email"
                        value={this.state.email.value}
                        onBlur={this.validateEmailAvailability}
                        // onChange={event =>this.handleInputChange(event, this.validateEmail)}
                      />
                    </InputGroup>
                    <Collapse isOpen={this.state.email.collapse}>
                      <p className="errorCollapseText">ایمیل نادرست است.</p>
                    </Collapse>
                  </FormGroup>
                  <FormGroup className="mb-2">
                    <InputGroup className="input-group-alternative">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-lock-circle-open" />
                        </InputGroupText>
                      </InputGroupAddon>

                      <Input
                        className="signup-modal-input"
                        placeholder="رمز عبور"
                        name="password"
                        type="password"
                      />
                    </InputGroup>
                    <Collapse isOpen={this.state.password.collapse}>
                      <p className="errorCollapseText">ایمیل نادرست است.</p>
                    </Collapse>
                  </FormGroup>
                  <FormGroup className="mb-2">
                    <InputGroup className="input-group-alternative">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-lock-circle-open" />
                        </InputGroupText>
                      </InputGroupAddon>

                      <Input
                        placeholder="تایید رمز عبور"
                        type="password"
                        className="signup-modal-input"
                      />
                    </InputGroup>
                    <Collapse isOpen={this.state.confirmPasswordCollapse}>
                      <p className="errorCollapseText">ایمیل نادرست است.</p>
                    </Collapse>
                  </FormGroup>
                  <div class="text-muted font-italic justify-content-center d-flex mt-0">
                    <small className="signup-modal-text">
                      کیفیت رمز عبور:
                      <span class="text-success font-weight-700 mr-2">قوی</span>
                    </small>
                  </div>
                  <Row className="text-center">
                    <Col>
                      <Button
                        className="my-4"
                        color="primary"
                        to="/dashboard"
                        tag={Link}
                      >
                        ایجاد
                      </Button>
                    </Col>
                  </Row>
                </Form>
              </CardBody>
            </Card>
          </div>
        </Modal>
      </div>
    );
  }

  validateEmail = email => {
    if (!email) {
      return {
        validateStatus: "error",
        errorMsg: "Email may not be empty"
      };
    }

    const EMAIL_REGEX = RegExp("[^@ ]+@[^@ ]+\\.[^@ ]+");
    if (!EMAIL_REGEX.test(email)) {
      return {
        validateStatus: "error",
        errorMsg: "Email not valid"
      };
    }

    if (email.length > 15) {
      return {
        validateStatus: "error",
        errorMsg: `Email is too long (Maximum ${15} characters allowed)`
      };
    }

    return {
      validateStatus: null,
      errorMsg: null
    };
  };
}

export default SignupButton;
